import PropTypes from 'prop-types';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import React, { useRef, useContext, useState, useEffect } from 'react';
import { UserContext } from '../../UserContext';
import { Redirect } from 'react-router-dom';
import RoomList from './RoomList';
import io from 'socket.io-client';


function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
};

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    };
}

let socket;
export default function BasicTabs() {
    const [value, setValue] = useState(0);
    const { user, setUser } = useContext(UserContext);
    const [room, setRoom] = useState('');
    const [room_id, setRoomId] = useState('')
    const [rooms, setRooms] = useState([]);
    const [showID, setShowID] = useState(false)
    const [copySuccess, setCopySuccess] = useState('');
    const textAreaRef = useRef(null);
    const ENDPT = 'localhost:5000';
    useEffect(() => {
        socket = io(ENDPT);
        return () => {
            socket.disconnect();
            socket.off();
        }
    }, [ENDPT])
    useEffect(() => {
        socket.on('output-rooms', rooms => {
            setRooms(rooms.filter(room => room.users.includes(user._id)))
        })

    }, [])
    useEffect(() => {
        socket.on('room-created', room => {
            setRoomId(room._id)
            setRooms([...rooms, room])
        })
    }, [rooms])
    useEffect(() => {
        socket.on('room-joined', room => {
            setRooms([...rooms, room])
        })
    }, [rooms])
    useEffect(() => {
        console.log(rooms)
    }, [rooms])

    const handleSubmit = e => {
        e.preventDefault();
        socket.emit('create-room', { name: room, user_id: user._id });
        console.log(room);
        setShowID(true);
        setRoom('');

    }
    function copyToClipboard(e) {
        textAreaRef.current.select();
        document.execCommand('copy');
        e.target.focus();
        setCopySuccess('Copied!');
    };
    const handleJoinSubmit = e => {
        e.preventDefault();
        socket.emit('join-room', { room_id, user_id: user._id });
        console.log(room);
        setRoom('');

    }
    if (!user) {
        return <Redirect to='/login' />
    }
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <Box sx={{ width: '100%' }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
                    <Tab label="Create room" {...a11yProps(0)} />
                    <Tab label="Join room" {...a11yProps(1)} />
                </Tabs>
            </Box>
            <TabPanel value={value} index={0}>
                <div>
                    <div className="row">
                        <div className="col s12 m6">
                            <div className="card blue-grey darken-1">
                                <div className="card-content white-text">
                                    <span className="card-title">Welcome {user ? user.name : ''}</span>
                                    <form onSubmit={handleSubmit}>
                                        <div className="row">
                                            <div className="input-field col s12">
                                                <input
                                                    placeholder="Enter a room name"
                                                    id="room" type="text" className="validate"
                                                    value={room}
                                                    onChange={e => setRoom(e.target.value)}
                                                />
                                                {showID ? <div><p>Your room ID is: <textarea
                                                    ref={textAreaRef}
                                                    value={room_id}
                                                /></p> 
                                                    <div>
                                                        <button onClick={copyToClipboard}>Copy</button>
                                                        {copySuccess}
                                                    </div>
                                                </div>: null}
                                                <label htmlFor="room">Room</label>
                                            </div>
                                        </div>
                                        <button className="btn">Create Room</button>
                                    </form>
                                </div>

                            </div>
                        </div>
                        <div className="col s6 m5 offset-1">
                            <RoomList rooms={rooms} />
                        </div>
                    </div>

                </div>
            </TabPanel>
            <TabPanel value={value} index={1}>
                <div>
                    <div className="row">
                        <div className="col s12 m6">
                            <div className="card blue-grey darken-1">
                                <div className="card-content white-text">
                                    <span className="card-title">Welcome {user ? user.name : ''}</span>
                                    <form onSubmit={handleJoinSubmit}>
                                        <div className="row">
                                            <div className="input-field col s12">
                                                <input
                                                    placeholder="Enter a room ID"
                                                    id="room" type="text" className="validate"
                                                    value={room_id}
                                                    onChange={e => setRoomId(e.target.value)}
                                                />
                                                <label htmlFor="room">Room ID</label>
                                            </div>
                                        </div>
                                        <button className="btn">Join Room</button>
                                    </form>
                                </div>

                            </div>
                        </div>
                        <div className="col s6 m5 offset-1">
                            <RoomList rooms={rooms} />
                        </div>
                    </div>

                </div>
            </TabPanel>
        </Box>
    );
}
